@testable import AdventOfCode2022
import XCTest

final class SeventeenTests: XCTestCase {
    let sut = Seventeen()

    func testFirstExample() throws {
        XCTAssertEqual(sut.run(with: testInput, rocks: 2022), 3068)
    }

    func testFirst() throws {
        XCTAssertEqual(sut.run(with: input, rocks: 2022), 3175)
    }

    func testSecondExample() {
        XCTAssertEqual(sut.run(with: testInput, rocks: 1000000000000), 1514285714288)
    }

    func testSecond() {
        XCTAssertEqual(sut.run(with: input, rocks: 1000000000000), 1555113636385)
    }
}

